/*
 * Assume 6<=z<=25, 6<=m<=15
 */
double interp_func(double z, double lnm)
{
    int zip = 0;
    int zin = 0;
    int mip = 0;
    int min = 0;
    double mmin = 6.0;
    double dm = 0.09;
    int i,j;

    //find indices in the table
    for (i = 0; i <= 2; i++){
        if (functions[i+1].redshift >= z) {
            zip = i;
            zin = i+1;
            for (j = 0; j <=100; j++) {
                if (lnm <= (mmin + (double)(j+1) *dm)){
                    mip = j;
                    min = j+1;
                }
            }
        }
    }
    
    //linearly interpolate value
    double pdz = (functions[zip].zeta[min] - functions[zip].zeta[mip])/dm;
    double pzeta = functions[zip].zeta[mip] + pdz*(lnm-(mmin*mip));
    double ndz = (functions[zin].zeta[min] - functions[zin].zeta[min])/dm;
    double nzeta = functions[zin].zeta[mip] + ndz*(lnm-(mmin*mip));
    double dz = (nzeta-pzeta)*(z-functions[zip].redshift);

    return pzeta+dz;
}
